import React, { useEffect, useState } from 'react';
import { IoStar } from 'react-icons/io5';
import './StarRating.css';

const StarRating = () => {
  const [clickedRating, setClickedRating] = useState(0); // holds true value of rating
  const [selectedRating, setSelectedRating] = useState(0);

  const handleMouseEnter = (hoveredStar) => {
    if (clickedRating > 0) return;
    setSelectedRating(hoveredStar);
    console.log('hovered rating is:', { selectedRating });
  };

  const handleMouseLeave = () => {
    if (clickedRating > 0) return;
    setSelectedRating(0);
  };

  const handleStarClick = (rating) => {
    console.log('clicked rating is:', rating);

    if (clickedRating > 0) {
      console.log('There is already a rating and it is:', clickedRating);

      return;
    } else {
      console.log('This is first time recording a value and it is:', clickedRating);
    }

    setClickedRating(rating);

    // console.log('clicked rating is:', { clickedRating });
  };

  // ! Why is interaction still possible after click? the cleanup function should delete the event listeners, once clickedRating changes

  // ! add event listener to container instead of each star
  useEffect(() => {
    const stars = document.querySelectorAll('.star');
    stars.forEach((star, index) => {
      star.addEventListener('mouseenter', () => handleMouseEnter(index + 1));
      star.addEventListener('mouseleave', handleMouseLeave);
      star.addEventListener('click', () => handleStarClick(index + 1));

      return () => {
        console.log('cleanup function');
        star.removeEventListener('mouseenter', () =>
          handleMouseEnter(index + 1)
        );
        star.removeEventListener('mouseleave', handleMouseLeave);
        star.removeEventListener('click', () => handleStarClick(index + 1));
      };
    });
  }, []); // No dependencies here, add event listeners once

  return (
    <div className='star-rating'>
      {[1, 2, 3, 4, 5].map((index) => (
        <IoStar
          key={index}
          className={`star ${
            clickedRating >= index || selectedRating >= index ? 'filled' : ''
          }`}
        />
      ))}
    </div>
  );
};

export default StarRating;
