import './App.css';
import { useContext, useEffect } from 'react';
import RestaurantsContext from './context/RestContext';
import RestCard from './components/RestCard';

function App() {

  const { fetchRestaurants } = useContext(RestaurantsContext);
  //destructuring to get fetchBooks function from the provider BooksContext

  let fetchedRestaurants = []

  useEffect(() => {
    fetchedRestaurants = fetchRestaurants();
  }, [fetchRestaurants]);
//is it better to call fetchBooks in app, or in context?

  return (
    <div>
      <RestList fetchedRestaurants = {fetchedRestaurants}/>
    </div>
  );
}

export default App;
