import { useContext } from 'react';
import { useParams } from 'react-router-dom';
import RestContext from '../context/RestContext';
import {IoStar,IoLocation, IoCall, IoLogoEuro} from 'react-icons/io5'

function RestDetails() {
  const { id } = useParams();
  const { restaurants } = useContext(RestContext);

  if (restaurants.length === 0) {
    return <div>Loading...</div>;
  }

  const rest = restaurants.find((rest) => rest.reference === id);

  if (!rest) {
    return <div>Restaurant not found.</div>;
  }

  console.log('restsurant details before return');

  return (
    <div className='rest-details'>
      <section className='rest-info'>
        <h1 className='details-name'>{rest.name}</h1>
        <p className='details-descr'>{rest.editorial_summary}</p>
          <section className='info'>
            <div className='tags-and-rating'>
              <ul className='Tags'>
                <li className='Tag'>Vegetarian</li>
                <li className='Tag'>Pet friendly</li>
              </ul>
              <div className="card-rating">
                <IoStar/>
                {rest.rating}
              </div> 
            </div> 
          </section>
      </section>
      <section className='rest-imgs'>
        <img
          src={`https://picsum.photos/seed/${rest.reference}/200/300`} alt="not loaded image"
          alt={rest.name}
          className='details-img-main'
        />
        <img
          src={`https://picsum.photos/seed/${rest.reference}baz/200/300`} alt="not loaded image"
          alt={rest.name}
          className='details-img-small'
        />
        <img
          src={`https://picsum.photos/seed/${rest.reference}/200/300`} alt="not loaded image"
          alt={rest.name}
          className='details-img-small'
        />
      </section>
    </div>
  );
}

export default RestDetails;
