import './ReviewCard.css'

function ReviewCard(review) {

  console.log('review card before return')
  console.log(review)
  const reviewNew = review.review

  let text = reviewNew.text;
  if (text.length > 18) {
      const shortName = text.substring(0, 18);
      text = `${shortName}...`;
  }


  return (
      <div className='review'>
        <div className='review-author'>
          <img
            src={reviewNew.profile_photo_url}
            alt={reviewNew.author_name}
            className='review-avatar'
          />
          <h3>{reviewNew.author_name}</h3>
        </div>
        <p className='review-text'>{reviewNew.text}</p>
      </div>
  );
}

export default ReviewCard;
