import './App.css';
import { useContext, useEffect } from 'react';
import RestaurantsContext from './context/RestContext';
import RestCard from './components/RestCard';

function App() {

  const { fetchBooks, books } = useContext(RestaurantsContext);
  //destructuring to get fetchBooks function from the provider BooksContext

  useEffect(() => {
    fetchBooks();
  }, [fetchBooks]);
//is it better to call fetchBooks in app, or in context?

  return (
    <div>
      {books}
      <RestCard/>
    </div>
  );
}

export default App;
