import { useContext } from 'react';
import { useParams } from 'react-router-dom';
import RestContext from '../context/RestContext';

function RestDetails() {
  const { id } = useParams();
  const { restaurants } = useContext(RestContext);

  if (restaurants.length === 0) {
    return <div>Loading...</div>;
  }

  const restaurant = restaurants.find((rest) => rest.reference === id);

  if (!restaurant) {
    return <div>Restaurant not found.</div>;
  }

  console.log('rest details before return');

  return (
    <div className='rest-details'>
      <section className='rest-info'>
        <sectionclass></sectionclass>
        <h1 className='details-name'>{restaurant.name}</h1>

        <p className='details-descr'>{restaurant.editorial_summary}</p>
      </section>
      <section className='rest-imgs'>
        <img
          src={restaurant.imageURL}
          alt={restaurant.name}
          className='w-full h-48 object-cover rounded'
        />
      </section>
    </div>
  );
}

export default RestDetails;
