import { IoStar, IoEyeOff, IoEyeOutline } from "react-icons/io5";


function RestCard ({rest}) {

  // for extraction from DB but not working
  // const urlPattern = /https:\/\/[^"]+/g;
  // const Url = rest.photos.match(urlPattern)


  let descr = rest.editorial_summary
  if (descr.length > 50) {
    const shortDescr = descr.substring(0,50)
    descr = `${shortDescr}...`
  }

  return (
    <div>
      <picture>
        <img src={`https://picsum.photos/seed/${rest.reference}/200/300`} alt="imageload failed"/>
      </picture>
      <h2>{rest.name}</h2>
      <p>{descr}</p>
      <div className="flex">
        <IoStar/> {rest.rating}
        {rest.hidden === 'Yes' ? <IoEyeOff/> : <IoEyeOutline/> }
      </div>



    </div>
  )
}

export default RestCard
